def quantite5F(array)
  quantite5=0
  array.each do |name|
    if name.length==6
      quantite5+=1
    end
  end
  puts quantite5
end

def quantitMajusculeF(array)
  quantitMajuscule=0
  array.each do |name|
    if name[1]==name[1].upcase
      quantitMajuscule+=1
    end
  end
  puts quantitMajuscule
end

def trierAlphabetique(array)
  array=array.sort
  puts array
end

def trierTaille(array)
  array=array.sort_by { |name| name.length }
  puts array
end

def position(array)
  puts "#{array.index("@epenser")}"
end

def classement(array)
  # les classer
  # pour taille -1
  # comparer tail n et n+1
  # si identiquer nom+=1
  # si diferent il y a nombre de cette tail et nombre=1
  array=array.sort_by { |name| name.length }
  quantite=1
  (array.length-1).times do |i|
    if array[i].length==array[i+1].length
      quantite+=1
    else
      puts " il y a #{quantite} noms de #{array[i].length-1} caracter"
      quantite=1
    end
  end
  if array[array.length-2]==array[array.length-1]
    puts "il y a #{quantite} noms de #{array[array.length-1].length-1} caracter"
  else
    puts "il y a 1 nom de #{array[array.length-1].length-1} caracter"
  end
end

def menu(array)
  puts "1. Combien y a-t-il de handle dans cette array ?"
  puts "2. Quel est le handle le plus court de cette liste ?"
  puts "3. Combien y-a-t'il de handle contenant 5 caractères "
  puts "4. Combien commencent par une majuscule ?"
  puts "5. Trie la liste de handle par ordre alphabétique."
  puts "6. Trie la liste de handle par taille des handle "
  puts "7. Quelle est la position dans l'array de la personne @epenser ?"
  puts "8. Sors-moi une répartition des handle par taille de ces derniers "
  choi=gets.chomp.to_i
  if choi==1
    nombrArray(array)
  elsif choi==2
    plusPetit(array)
  elsif choi==3
    quantite5F(array)
  elsif choi==4
    quantitMajusculeF(array)
  elsif choi==5
    trierAlphabetique(array)
  elsif choi==6
    trierTaille(array)
  elsif choi==7
    position(array)
  elsif choi==8
    classement(array)
  else
    menu(array)
  end
end

